<script>
  $(document).ready(function () {
    // Get the value of the preferred_language cookie
    var preferredLanguage = getCookie('preferred_language')

    // If the preferred_language cookie exists and its value is 'en' or 'ne',
    // set the selected option in the dropdown accordingly
    if (preferredLanguage && (preferredLanguage === 'en' || preferredLanguage === 'ne')) {
      $('#language-select option[value="' + preferredLanguage + '"]').prop('selected', true)
    }

    // Function to get cookie value by name
    function getCookie(name) {
      var value = '; ' + document.cookie
      var parts = value.split('; ' + name + '=')
      if (parts.length == 2) return parts.pop().split(';').shift()
    }

    /* Hide flags based on selected language
    $('#language-select').change(function() {
      var selectedLanguage = $(this).val();
      if (selectedLanguage === 'ne') {
        $('.nep-flag').removeClass('d-none');
        $('.eng-flag').addClass('d-none');
      } else if (selectedLanguage === 'en') {
        $('.eng-flag').removeClass('d-none');
        $('.nep-flag').addClass('d-none');
      }
    }); */

    /* Initially hide flags based on the initial selected language
    var initialSelectedLanguage = $('#language-select').val();
    if (initialSelectedLanguage === 'ne') {
      $('.nep-flag').removeClass('d-none');
      $('.eng-flag').addClass('d-none');
    } else if (initialSelectedLanguage === 'en') {
      $('.eng-flag').removeClass('d-none');
      $('.nep-flag').addClass('d-none');
    } */
})
</script>

