          const dataPoints = [];

          chartDataParsed.forEach((data, index) => {
              let group = data.group;
              let category = data.category;
              let value = data.value;
              let color = data.color;
              dataPoints.push({ group, category, value, color });
          });
          generateChart(dataPoints);
      }


      function generateChart(dataPoints) {
          const chartType = 'line';
          renderChart(dataPoints, chartType);
      }

      function transformDataForChart(dataPoints, chartType) {
      const labels = [...new Set(dataPoints.map(dp => dp.group))];
      const categories = [...new Set(dataPoints.map(dp => dp.category))];
      const datasets = categories.map(category => {
          const data = labels.map(label => {
              const point = dataPoints.find(dp => dp.category === category && dp.group === label);
              return point ? point.value : 0;
          });
          const backgroundColors = labels.map(label => {
              const point = dataPoints.find(dp => dp.category === category && dp.group === label);
              return point ? point.color : randomBrightColor();
          });
          return {
              label: category,
              data,
              backgroundColor: backgroundColors,
              borderColor: backgroundColors.map(color => color), // Assuming border color same as background color
              borderWidth: 1,
              fill: chartType === 'line' ? false : true,
          };
      });
      return { labels, datasets };
  }


  function hslToHex(h, s, l) {
      l /= 100;
      const a = s * Math.min(l, 1 - l) / 100;
      const f = n => {
          const k = (n + h / 30) % 12;
          const color = l - a * Math.max(Math.min(k - 3, 9 - k, 1), -1);
          return Math.round(255 * color).toString(16).padStart(2, '0');   // Convert to Hex and format
      };
      return `#${f(0)}${f(8)}${f(4)}`;
  }


  function randomColor() {
      const h = Math.floor(Math.random() * 360);
      return `hsl(${h}, 100%, 75%)`;
  }

  function randomBrightColor() {
      const h = Math.floor(Math.random() * 360);
      return hslToHex(h, 100, 75)
  }


  function renderChart(dataPoints, chartType) {
      const { labels, datasets } = transformDataForChart(dataPoints, chartType);


      const ctx_4 = document.getElementById('lineChart-4').getContext('2d');


      window.userCustomChartInstance = new Chart(ctx_4, {
          type: chartType,
          data: { labels, datasets },
          options: {
              scales: {
                x: {
                    ticks: {
                        font: {
                            size: 10,
                            family:'vazir'
                        }
                    }
                },
                y: {
                    ticks: {
                        font: {
                            size: 10,
                            family:'vazir'
                        }
                    }
                }
              }
          }
      });
  }

  });
</script>







